/*!50003 DROP PROCEDURE IF EXISTS `SP_ACTION_FOLLOW` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8 */ ;
/*!50003 SET character_set_results = utf8 */ ;
/*!50003 SET collation_connection  = utf8_general_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_ACTION_FOLLOW`( action_status nvarchar(10) ,main_user nvarchar (100) ,
 another_user_email nvarchar(100) , out result int )
begin
         if  action_status = 'insert'
         then 
            insert into follow (email,followingEmail) values (main_user,another_user_email);
			set result = 1 ; 
		 elseif  action_status = 'delete'
         then 
           delete from follow where email = main_user and followingEmail = another_user_email ;
            set result = 1 ; 
		 else 
             set result = 0 ; 
         end if ; 
end ;;
DELIMITER ;
