CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_CHECK_STATUS_MEAL_LIKE`( inputEmail nvarchar(50) , inputMealID int  ,out result int)
begin 
	   if   exists (select * from favorite where favorite.mealID = inputMealID and 
						favorite.email  = inputEmail ) 
		then 
          set result = 1 ;
		else 
		  set result = 0 ;
		end if ; 
end ;;
