CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_INS_fbUSER`(v_email varchar(100), v_password varchar(20), v_firstName Nvarchar(50),
    v_lastName Nvarchar(50), v_fullName Nvarchar(100), v_profilePicture text, out v_result varchar(100))
BEGIN 
    IF (not EXISTS(SELECT * FROM `USER` WHERE `email` = v_email)) THEN
       INSERT INTO `USER` 
            (`email`,`password`,`firstName`,`lastName`,`fullName`,`profilePicture`) 
          VALUES 
            (v_email,v_password,v_firstName,v_lastName,v_fullName,v_profilePicture);
             set v_result =   (SELECT  User.fullName from User where email = v_email );  
	else 
     set v_result =   (SELECT  User.fullName from User where email = v_email );  
	end if ;
          
	IF(ROW_COUNT()=0) then
		BEGIN
			set v_result = 0;
			ROLLBACK;
		END;
	end if;
	

END ;;
