CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_LIKE_MEAL`(actionLike nvarchar(50) ,input_mealID int , 
 input_email nvarchar(50) , out result int )
begin 
	declare number_ofLikes INT default 0;
    set number_ofLikes = (select numberOfLike from meal where mealID = input_mealID) ; 
    if number_ofLikes is NULL 
    then 
		set number_ofLikes = 0 ; 
	end if;
	if  actionLike = 'LIKE' 
    then 
		update meal set numberOfLike = (number_ofLikes + 1) where mealID = input_mealID ; 
		insert into favorite (email,mealID ) values (input_email,input_mealID) ; 
    elseif actionLike = 'UNLIKE'
    then 
		update meal set numberOfLike = (number_ofLikes - 1) where mealID = input_mealID ; 
		delete from Favorite where email = input_email and mealID = input_mealID ; 
	else 
		set result = 0 ; 
    end if ;
	set result = 1 ; 
end ;;
DELIMITER ;
