CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_Update_fbUSER`(v_email varchar(100),v_fullName Nvarchar(100), out v_result varchar(100))
BEGIN 
    IF (not EXISTS(SELECT * FROM `USER` WHERE `email` = v_email)) THEN
       set v_result = 0 ; 
	else 
        update user set fullName = v_fullName where user.email = v_email ;
	   set v_result = 1 ; 
	end if ;
          
	IF(ROW_COUNT()=0) then
		BEGIN
			set v_result = 0;
			ROLLBACK;
		END;
	end if;
	

END ;;
